import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const PlayerContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
  border: 2px solid black;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const TableContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  border-bottom: 1px solid #ccc;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  font-size: 12px;
`;

const TableHeader = styled.thead`
  background-color: #f0f0f0;
  position: sticky;
  top: 0;
  z-index: 1;
`;

const HeaderRow = styled.tr`
  border-bottom: 1px solid #ccc;
`;

const HeaderCell = styled.th<{ sortable?: boolean }>`
  padding: 8px 12px;
  text-align: left;
  font-weight: bold;
  color: #333;
  border-right: 1px solid #ccc;
  cursor: ${props => props.sortable ? 'pointer' : 'default'};
  user-select: none;

  &:hover {
    background-color: ${props => props.sortable ? '#e0e0e0' : 'transparent'};
  }

  &:last-child {
    border-right: none;
  }
`;

const SortIcon = styled.span`
  margin-left: 4px;
  font-size: 10px;
`;

const TableBody = styled.tbody``;

const TableRow = styled.tr<{ isSelected: boolean; isPlaying: boolean }>`
  background-color: ${props => props.isPlaying ? '#e3f2fd' : props.isSelected ? '#f5f5f5' : 'white'};
  border-bottom: 1px solid #eee;
  cursor: pointer;

  &:hover {
    background-color: ${props => props.isPlaying ? '#e3f2fd' : '#f8f8f8'};
  }
`;

const TableCell = styled.td`
  padding: 6px 12px;
  border-right: 1px solid #eee;
  vertical-align: middle;

  &:last-child {
    border-right: none;
  }
`;

const Checkbox = styled.input`
  margin: 0;
  cursor: pointer;
`;

const PlayButton = styled.button<{ isPlaying: boolean }>`
  background: none;
  border: none;
  cursor: pointer;
  padding: 2px;
  font-size: 12px;
  color: ${props => props.isPlaying ? '#1976d2' : '#666'};

  &:hover {
    color: #1976d2;
  }
`;

const ControlsContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 12px;
  background-color: #f8f8f8;
  border-top: 1px solid #ccc;
  gap: 12px;
`;

const PlayPauseButton = styled.button`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 1px solid #ccc;
  background: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const ProgressBar = styled.div`
  flex: 1;
  height: 4px;
  background-color: #ddd;
  border-radius: 2px;
  position: relative;
  cursor: pointer;
  user-select: none;
`;

const ProgressFill = styled.div<{ progress: number }>`
  height: 100%;
  width: ${props => props.progress}%;
  background-color: #1976d2;
  border-radius: 2px;
  transition: width 0.1s ease;
`;

const TimeDisplay = styled.span`
  font-size: 11px;
  color: #666;
  min-width: 60px;
  text-align: center;
`;

const VolumeControl = styled.input`
  width: 80px;
`;

interface DemoFile {
  name: string;
  filename: string;
  duration: string;
  size: string;
}

const DemosPlayer: React.FC = () => {
  const [selectedTracks, setSelectedTracks] = useState<Set<string>>(new Set());
  const [currentTrack, setCurrentTrack] = useState<string | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [sortBy, setSortBy] = useState<'name' | 'duration'>('name');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

  const audioRef = useRef<HTMLAudioElement>(null);

  const demos: DemoFile[] = [
    { name: "You are driving me insane", filename: "You are driving me insane.m4a", duration: "3:45", size: "1.8MB" },
    { name: "Sunburnt shrederino", filename: "Sunburnt shrederino.m4a", duration: "2:30", size: "1.2MB" },
    { name: "Song idea", filename: "Song idea.m4a", duration: "2:15", size: "1.2MB" },
    { name: "Girl you lol fine bridge", filename: "Girl you lol fine bridge_.m4a", duration: "1:20", size: "459KB" },
    { name: "Hideaway updated demo", filename: "Hideaway updated demo.m4a", duration: "2:45", size: "1.0MB" },
    { name: "Hmmm (demo)", filename: "Hmmm (demo).m4a", duration: "1:50", size: "636KB" },
    { name: "Ya seeeeee (demo)", filename: "Ya seeeeee (demo).m4a", duration: "2:55", size: "1.1MB" }
  ];

  const sortedDemos = [...demos].sort((a, b) => {
    const aValue = sortBy === 'name' ? a.name.toLowerCase() : a.duration;
    const bValue = sortBy === 'name' ? b.name.toLowerCase() : b.duration;

    if (sortOrder === 'asc') {
      return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
    } else {
      return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
    }
  });

  const handleSort = (column: 'name' | 'duration') => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const handleTrackSelect = (filename: string) => {
    const newSelected = new Set(selectedTracks);
    if (newSelected.has(filename)) {
      newSelected.delete(filename);
    } else {
      newSelected.add(filename);
    }
    setSelectedTracks(newSelected);
  };

  const handlePlayTrack = (filename: string) => {
    if (currentTrack === filename && isPlaying) {
      // Pause current track
      audioRef.current?.pause();
      setIsPlaying(false);
    } else {
      // Play new track
      setCurrentTrack(filename);
      setIsPlaying(true);
      if (audioRef.current) {
        audioRef.current.src = `/demos/${filename}`;
        audioRef.current.play();
      }
    }
  };

  const handlePlayPause = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleProgressClick = (e: React.MouseEvent) => {
    if (audioRef.current && duration > 0) {
      const rect = e.currentTarget.getBoundingClientRect();
      const clickX = e.clientX - rect.left;
      const newTime = (clickX / rect.width) * duration;
      audioRef.current.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };

  const handleProgressDrag = (e: React.MouseEvent) => {
    e.preventDefault();
    if (audioRef.current && duration > 0) {
      const rect = e.currentTarget.getBoundingClientRect();
      const clickX = e.clientX - rect.left;
      const newTime = Math.max(0, Math.min(duration, (clickX / rect.width) * duration));
      audioRef.current.currentTime = newTime;
      setCurrentTime(newTime);
    }
  };

  return (
    <PlayerContainer>
      <TableContainer>
        <Table>
          <TableHeader>
            <HeaderRow>
              <HeaderCell style={{ width: '20px' }}></HeaderCell>
              <HeaderCell style={{ width: '20px' }}></HeaderCell>
              <HeaderCell 
                sortable 
                onClick={() => handleSort('name')}
                style={{ width: '40%' }}
              >
                Song Name
                {sortBy === 'name' && (
                  <SortIcon>{sortOrder === 'asc' ? '▲' : '▼'}</SortIcon>
                )}
              </HeaderCell>
              <HeaderCell 
                sortable 
                onClick={() => handleSort('duration')}
                style={{ width: '15%' }}
              >
                Time
                {sortBy === 'duration' && (
                  <SortIcon>{sortOrder === 'asc' ? '▲' : '▼'}</SortIcon>
                )}
              </HeaderCell>
              <HeaderCell style={{ width: '25%' }}>Artist</HeaderCell>
              <HeaderCell style={{ width: '20%' }}>Album</HeaderCell>
            </HeaderRow>
          </TableHeader>
          <TableBody>
            {sortedDemos.map((demo) => (
              <TableRow 
                key={demo.filename}
                isSelected={selectedTracks.has(demo.filename)}
                isPlaying={currentTrack === demo.filename}
                onClick={() => handlePlayTrack(demo.filename)}
              >
                <TableCell>
                  <Checkbox 
                    type="checkbox"
                    checked={selectedTracks.has(demo.filename)}
                    onChange={(e) => {
                      e.stopPropagation();
                      handleTrackSelect(demo.filename);
                    }}
                  />
                </TableCell>
                <TableCell>
                  <PlayButton 
                    isPlaying={currentTrack === demo.filename && isPlaying}
                    onClick={(e) => {
                      e.stopPropagation();
                      handlePlayTrack(demo.filename);
                    }}
                  >
                    {currentTrack === demo.filename && isPlaying ? '⏸' : '▶'}
                  </PlayButton>
                </TableCell>
                <TableCell>{demo.name}</TableCell>
                <TableCell>{demo.duration}</TableCell>
                <TableCell>Juan Zhingre</TableCell>
                <TableCell>demos</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <ControlsContainer>
        <PlayPauseButton onClick={handlePlayPause}>
          {isPlaying ? '⏸' : '▶'}
        </PlayPauseButton>
        <ProgressBar onClick={handleProgressClick}>
          <ProgressFill progress={duration > 0 ? (currentTime / duration) * 100 : 0} />
        </ProgressBar>
        <TimeDisplay>
          {formatTime(currentTime)} / {formatTime(duration)}
        </TimeDisplay>
        <VolumeControl 
          type="range" 
          min="0" 
          max="1" 
          step="0.1" 
          defaultValue="0.7"
          onChange={(e) => {
            if (audioRef.current) {
              audioRef.current.volume = parseFloat(e.target.value);
            }
          }}
        />
      </ControlsContainer>

      <audio
        ref={audioRef}
        onTimeUpdate={() => {
          if (audioRef.current) {
            setCurrentTime(audioRef.current.currentTime);
          }
        }}
        onLoadedMetadata={() => {
          if (audioRef.current) {
            setDuration(audioRef.current.duration);
          }
        }}
        onEnded={() => {
          setIsPlaying(false);
          setCurrentTime(0);
        }}
      />
    </PlayerContainer>
  );
};

export default DemosPlayer;
