import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import Icon from '../Icon/Icon';
import Window from '../Window/Window';
import SimpleGame from '../Window/SimpleGame';
import { Icon as IconType, Window as WindowType, DesktopState } from '../../types/desktop';

const DesktopContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(135deg, #ff8c42, #ffb347);
  background-image: 
    radial-gradient(circle at 25% 25%, rgba(255, 140, 66, 0.8) 0%, transparent 50%),
    radial-gradient(circle at 75% 75%, rgba(255, 179, 71, 0.8) 0%, transparent 50%);
  position: relative;
  overflow: hidden;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 40px,
      rgba(255, 255, 255, 0.05) 40px,
      rgba(255, 255, 255, 0.05) 80px
    );
  pointer-events: none;
`;

const DesktopTitle = styled.div`
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 48px;
  font-weight: bold;
  color: #333;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  z-index: 1;
  pointer-events: none;
`;

const Desktop: React.FC = () => {
  const [desktopState, setDesktopState] = useState<DesktopState>({
    icons: [
      {
        id: 'apple-music',
        name: 'Apple Music',
        type: 'app',
        position: { x: 50, y: 150 },
        icon: '‚ô™',
        color: '#007AFF'
      },
      {
        id: 'spotify',
        name: 'Spotify',
        type: 'app',
        position: { x: 50, y: 280 },
        icon: '‚ô´',
        color: '#1DB954'
      },
      {
        id: 'videos',
        name: 'Videos',
        type: 'folder',
        position: { x: 50, y: 410 },
        icon: '‚ñ∂',
        color: '#FF0000'
      },
      {
        id: 'misc',
        name: 'Misc',
        type: 'folder',
        position: { x: 50, y: 540 },
        icon: 'üìÅ',
        color: '#FFD700'
      },
      {
        id: 'my-shop',
        name: 'My Shop',
        type: 'app',
        position: { x: window.innerWidth - 150, y: 150 },
        icon: 'üõçÔ∏è',
        color: '#87CEEB'
      },
      {
        id: 'hw-hq',
        name: 'HW HQ',
        type: 'app',
        position: { x: window.innerWidth - 150, y: 280 },
        icon: 'üìù',
        color: '#87CEEB'
      },
      {
        id: 'games',
        name: 'Games',
        type: 'folder',
        position: { x: window.innerWidth - 150, y: 410 },
        icon: 'üéÆ',
        color: '#FF6B6B'
      },
      {
        id: 'trash',
        name: 'Trash',
        type: 'folder',
        position: { x: window.innerWidth - 150, y: window.innerHeight - 150 },
        icon: 'üóëÔ∏è',
        color: '#808080'
      }
    ],
    windows: [],
    activeWindowId: null,
    background: 'dog-pattern'
  });

  const handleIconClick = useCallback((icon: IconType) => {
    if (icon.id === 'games') {
      // Open games window with the actual game
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: 'Games - Icon Collector',
        type: 'game',
        position: { x: 150, y: 150 },
        size: { width: 500, height: 400 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <SimpleGame />
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    } else if (icon.type === 'folder') {
      // Open folder window
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: icon.name,
        type: 'folder',
        position: { x: 100, y: 100 },
        size: { width: 600, height: 400 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <div>This is the {icon.name} folder!</div>
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    } else if (icon.type === 'app') {
      // Open app window
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: icon.name,
        type: 'app',
        position: { x: 150, y: 150 },
        size: { width: 800, height: 600 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <div>Welcome to {icon.name}!</div>
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    }
  }, []);

  const handleWindowClose = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.filter(w => w.id !== windowId),
      activeWindowId: prev.activeWindowId === windowId ? null : prev.activeWindowId
    }));
  }, []);

  const handleWindowMinimize = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, isMinimized: !w.isMinimized } : w
      )
    }));
  }, []);

  const handleWindowMaximize = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, isMaximized: !w.isMaximized } : w
      )
    }));
  }, []);

  const handleWindowMove = useCallback((windowId: string, x: number, y: number) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, position: { x, y } } : w
      )
    }));
  }, []);

  const handleWindowResize = useCallback((windowId: string, width: number, height: number) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, size: { width, height } } : w
      )
    }));
  }, []);

  const handleWindowFocus = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      activeWindowId: windowId,
      windows: prev.windows.map(w => ({
        ...w,
        zIndex: w.id === windowId ? Date.now() : w.zIndex
      }))
    }));
  }, []);

  return (
    <DesktopContainer>
      <BackgroundPattern />
      <DesktopTitle>H.W.</DesktopTitle>

      {/* Desktop Icons */}
      {desktopState.icons.map(icon => (
        <Icon
          key={icon.id}
          icon={icon}
          onClick={() => handleIconClick(icon)}
          isSelected={false}
        />
      ))}

      {/* Windows */}
      {desktopState.windows.map(window => (
        <Window
          key={window.id}
          window={window}
          onClose={handleWindowClose}
          onMinimize={handleWindowMinimize}
          onMaximize={handleWindowMaximize}
          onMove={handleWindowMove}
          onResize={handleWindowResize}
          onFocus={handleWindowFocus}
        >
          {window.content}
        </Window>
      ))}
    </DesktopContainer>
  );
};

export default Desktop;
