import React from 'react';
import styled from 'styled-components';

interface FolderContentProps {
  folderId: string;
  folderName: string;
}

const ContentContainer = styled.div`
  height: 100%;
  padding: 20px;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
`;

const FolderIcon = styled.div`
  font-size: 64px;
  margin-bottom: 20px;
`;

const FolderTitle = styled.h2`
  margin: 0 0 15px 0;
  color: #333;
  font-size: 24px;
  font-weight: 600;
`;

const FolderDescription = styled.p`
  margin: 0 0 20px 0;
  color: #666;
  font-size: 16px;
  max-width: 400px;
  line-height: 1.5;
`;

const FolderContent: React.FC<FolderContentProps> = ({ folderId, folderName }) => {
  const getFolderContent = () => {
    switch (folderId) {
      case 'music':
        return {
          icon: 'üéµ',
          title: 'Music Collection',
          description: 'Your personal music library and playlists. Click to browse your favorite tracks and discover new music.'
        };

      case 'the-archive':
        return {
          icon: 'üìö',
          title: 'The Archive',
          description: 'A curated collection of memories, documents, and important files. Your digital time capsule.'
        };

      case 'sculpter':
        return {
          icon: 'üé®',
          title: 'Sculpter',
          description: 'Your creative workspace for digital art, designs, and artistic projects. Let your imagination flow.'
        };

      case 'about':
        return {
          icon: '‚ÑπÔ∏è',
          title: 'About',
          description: 'Learn more about this desktop, its features, and how to customize it to your needs.'
        };

      default:
        return {
          icon: 'üìÅ',
          title: folderName,
          description: 'This folder contains various files and documents.'
        };
    }
  };

  const content = getFolderContent();

  return (
    <ContentContainer>
      <FolderIcon>{content.icon}</FolderIcon>
      <FolderTitle>{content.title}</FolderTitle>
      <FolderDescription>{content.description}</FolderDescription>
    </ContentContainer>
  );
};

export default FolderContent;
