import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import Icon from '../Icon/Icon';
import Window from '../Window/Window';
import SimpleGame from '../Window/SimpleGame';
import PhotoGallery from '../Window/PhotoGallery';
import CollectionViewer from '../Window/CollectionViewer';
import FullImageViewer from '../Window/FullImageViewer';
import FolderContent from '../Window/FolderContent';
import { Icon as IconType, Window as WindowType, DesktopState } from '../../types/desktop';
import { photoData } from '../../data/photos';

const DesktopContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url('/images/california summer/Cali-Background.jpg');
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  position: relative;
  overflow: hidden;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const BackgroundOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.2);
  pointer-events: none;
`;

const DesktopTitle = styled.div`
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 48px;
  font-weight: bold;
  color: white;
  text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.8);
  z-index: 1;
  pointer-events: none;
`;

const Desktop: React.FC = () => {
  const [desktopState, setDesktopState] = useState<DesktopState>({
          icons: [
        {
          id: 'music',
          name: 'music',
          type: 'folder',
          position: { x: 50, y: 150 },
          icon: '/images/icons/SoundCloud.png',
          color: '#007AFF'
        },
        {
          id: 'the-archive',
          name: 'the archive',
          type: 'folder',
          position: { x: 50, y: 280 },
          icon: '/images/icons/WinRAR.png',
          color: '#8B4513'
        },
        {
          id: 'sculpter',
          name: 'sculpter',
          type: 'folder',
          position: { x: 50, y: 410 },
          icon: '/images/icons/Air Europa.png',
          color: '#FF6B6B'
        },
        {
          id: 'about',
          name: 'about',
          type: 'folder',
          position: { x: 50, y: 540 },
          icon: '/images/icons/Toggl Hire.png',
          color: '#4CAF50'
        },
        {
          id: 'trash',
          name: 'trash',
          type: 'folder',
          position: { x: window.innerWidth - 150, y: window.innerHeight - 150 },
          icon: '🗑️',
          color: '#808080'
        }
      ],
    windows: [],
    activeWindowId: null,
    background: 'dog-pattern'
  });

  const handleIconMove = useCallback((iconId: string, x: number, y: number) => {
    setDesktopState(prev => {
      const iconIndex = prev.icons.findIndex(icon => icon.id === iconId);
      if (iconIndex === -1) return prev;

      const newIcons = [...prev.icons];
      newIcons[iconIndex] = {
        ...newIcons[iconIndex],
        position: { x, y }
      };

      return {
        ...prev,
        icons: newIcons
      };
    });
  }, []);

  const handleOpenCollection = useCallback((collection: any) => {
    const newWindow: WindowType = {
      id: `collection-${collection.id}`,
      title: `${collection.name} - Photos`,
      type: 'folder',
      position: { x: 150, y: 150 },
      size: { width: 1000, height: 700 },
      isMinimized: false,
      isMaximized: false,
      isOpen: true,
      zIndex: Date.now(),
      content: <CollectionViewer collection={collection} onOpenPhoto={(photo) => handleOpenPhoto(photo)} />
    };

    setDesktopState(prev => ({
      ...prev,
      windows: [...prev.windows, newWindow],
      activeWindowId: newWindow.id
    }));
  }, []);

  const handleOpenPhoto = useCallback((photo: any) => {
    const newWindow: WindowType = {
      id: `photo-${photo.id}`,
      title: `${photo.name} - Full View`,
      type: 'app',
      position: { x: 200, y: 200 },
      size: { width: 900, height: 700 },
      isMinimized: false,
      isMaximized: false,
      isOpen: true,
      zIndex: Date.now(),
      content: <FullImageViewer 
        photo={photo} 
        onClose={() => handleWindowClose(`photo-${photo.id}`)}
        onNavigate={(direction) => handlePhotoNavigate(photo, direction)}
      />
    };

    setDesktopState(prev => ({
      ...prev,
      windows: [...prev.windows, newWindow],
      activeWindowId: newWindow.id
    }));
  }, []);

  const handlePhotoNavigate = useCallback((currentPhoto: any, direction: 'prev' | 'next') => {
    // For now, just close the current window and open a new one
    // This is simpler and avoids complex state updates
    const photos = photoData.filter(p => p.collection === currentPhoto.collection);
    const currentIndex = photos.findIndex(p => p.id === currentPhoto.id);
    let newIndex = direction === 'next' ? currentIndex + 1 : currentIndex - 1;

    // Wrap around
    if (newIndex < 0) newIndex = photos.length - 1;
    if (newIndex >= photos.length) newIndex = 0;

    const newPhoto = photos[newIndex];

    // Close current window and open new one
    // We'll implement this properly after fixing the structure
    console.log('Navigate to:', newPhoto.name, direction);
  }, []);

  const handleIconClick = useCallback((icon: IconType) => {
    if (icon.id === 'the-archive') {
      // Open photo gallery for the Archive icon
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: 'Photo Gallery',
        type: 'app',
        position: { x: 100, y: 100 },
        size: { width: 1000, height: 700 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <PhotoGallery onOpenCollection={handleOpenCollection} />
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    } else if (icon.type === 'folder') {
      // Open folder window with custom content
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: icon.name,
        type: 'folder',
        position: { x: 100, y: 100 },
        size: { width: 600, height: 400 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <FolderContent folderId={icon.id} folderName={icon.name} />
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    } else if (icon.type === 'app') {
      // Open app window
      const newWindow: WindowType = {
        id: `window-${icon.id}`,
        title: icon.name,
        type: 'app',
        position: { x: 150, y: 150 },
        size: { width: 800, height: 600 },
        isMinimized: false,
        isMaximized: false,
        isOpen: true,
        zIndex: Date.now(),
        content: <div>Welcome to {icon.name}!</div>
      };

      setDesktopState(prev => ({
        ...prev,
        windows: [...prev.windows, newWindow],
        activeWindowId: newWindow.id
      }));
    }
  }, []);

  const handleWindowClose = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.filter(w => w.id !== windowId),
      activeWindowId: prev.activeWindowId === windowId ? null : prev.activeWindowId
    }));
  }, []);

  const handleWindowMinimize = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, isMinimized: !w.isMinimized } : w
      )
    }));
  }, []);

  const handleWindowMaximize = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, isMaximized: !w.isMaximized } : w
      )
    }));
  }, []);

  const handleWindowMove = useCallback((windowId: string, x: number, y: number) => {
    // Use functional update to avoid stale state issues
    setDesktopState(prev => {
      const windowIndex = prev.windows.findIndex(w => w.id === windowId);
      if (windowIndex === -1) return prev;

      const newWindows = [...prev.windows];
      newWindows[windowIndex] = {
        ...newWindows[windowIndex],
        position: { x, y }
      };

      return {
        ...prev,
        windows: newWindows
      };
    });
  }, []);

  const handleWindowResize = useCallback((windowId: string, width: number, height: number) => {
    setDesktopState(prev => ({
      ...prev,
      windows: prev.windows.map(w => 
        w.id === windowId ? { ...w, size: { width, height } } : w
      )
    }));
  }, []);

  const handleWindowFocus = useCallback((windowId: string) => {
    setDesktopState(prev => ({
      ...prev,
      activeWindowId: windowId,
      windows: prev.windows.map(w => ({
        ...w,
        zIndex: w.id === windowId ? Date.now() : w.zIndex
      }))
    }));
  }, []);

  return (
    <DesktopContainer>
      <BackgroundOverlay />
      <DesktopTitle>Juan Zhingre</DesktopTitle>

      {/* Desktop Icons */}
      {desktopState.icons.map(icon => (
        <Icon
          key={icon.id}
          icon={icon}
          onClick={() => handleIconClick(icon)}
          onMove={handleIconMove}
          isSelected={false}
        />
      ))}

      {/* Windows */}
      {desktopState.windows.map(window => (
        <Window
          key={window.id}
          window={window}
          onClose={handleWindowClose}
          onMinimize={handleWindowMinimize}
          onMaximize={handleWindowMaximize}
          onMove={handleWindowMove}
          onResize={handleWindowResize}
          onFocus={handleWindowFocus}
        >
          {window.content}
        </Window>
      ))}
    </DesktopContainer>
  );
};

export default Desktop;
