import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { Window as WindowType } from '../../types/desktop';

interface WindowProps {
  window: WindowType;
  onClose: (id: string) => void;
  onMinimize: (id: string) => void;
  onMaximize: (id: string) => void;
  onMove: (id: string, x: number, y: number) => void;
  onResize: (id: string, width: number, height: number) => void;
  onFocus: (id: string) => void;
  children?: React.ReactNode;
}

const WindowContainer = styled.div<{ 
  isMinimized: boolean; 
  isMaximized: boolean;
  zIndex: number;
  x: number;
  y: number;
  width: number;
  height: number;
}>`
  position: absolute;
  left: ${props => props.isMinimized ? -1000 : props.x}px;
  top: ${props => props.isMinimized ? -1000 : props.y}px;
  width: ${props => props.isMaximized ? '100vw' : props.width}px;
  height: ${props => props.isMaximized ? '100vh' : props.height}px;
  background: white;
  border-radius: 8px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  z-index: ${props => props.zIndex};
  display: flex;
  flex-direction: column;
  overflow: hidden;
  transition: ${props => props.isMinimized ? 'none' : 'all 0.2s ease'};
`;

const TitleBar = styled.div`
  background: linear-gradient(135deg, #4a90e2, #357abd);
  color: white;
  padding: 8px 12px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  cursor: move;
  user-select: none;
  border-radius: 8px 8px 0 0;
`;

const Title = styled.div`
  font-weight: 600;
  font-size: 14px;
`;

const WindowControls = styled.div`
  display: flex;
  gap: 4px;
`;

const ControlButton = styled.button<{ type: 'minimize' | 'maximize' | 'close' }>`
  width: 16px;
  height: 16px;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;
  font-weight: bold;
  color: white;

  background-color: ${props => {
    switch (props.type) {
      case 'minimize': return '#ffbd2e';
      case 'maximize': return '#28ca42';
      case 'close': return '#ff5f57';
      default: return '#ccc';
    }
  }};

  &:hover {
    filter: brightness(1.1);
  }
`;

const WindowContent = styled.div`
  flex: 1;
  padding: 16px;
  overflow: auto;
`;

const Window: React.FC<WindowProps> = ({ 
  window, 
  onClose, 
  onMinimize, 
  onMaximize, 
  onMove, 
  onResize, 
  onFocus,
  children 
}) => {
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [isResizing, setIsResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState({ x: 0, y: 0, width: 0, height: 0 });

  const windowRef = useRef<HTMLDivElement>(null);

  const handleMouseDown = (e: React.MouseEvent) => {
    onFocus(window.id);

    if (e.target === windowRef.current?.querySelector('[data-title-bar]')) {
      setIsDragging(true);
      const rect = windowRef.current.getBoundingClientRect();
      setDragOffset({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  const handleMouseMove = (e: MouseEvent) => {
    if (isDragging) {
      const newX = e.clientX - dragOffset.x;
      const newY = e.clientY - dragOffset.y;
      onMove(window.id, newX, newY);
    }

    if (isResizing) {
      const newWidth = Math.max(300, resizeStart.width + (e.clientX - resizeStart.x));
      const newHeight = Math.max(200, resizeStart.height + (e.clientY - resizeStart.y));
      onResize(window.id, newWidth, newHeight);
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    setIsResizing(false);
  };

  React.useEffect(() => {
    if (isDragging || isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, isResizing, dragOffset, resizeStart]);

  const handleResizeStart = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsResizing(true);
    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      width: window.size.width,
      height: window.size.height
    });
  };

  return (
    <WindowContainer
      ref={windowRef}
      isMinimized={window.isMinimized}
      isMaximized={window.isMaximized}
      zIndex={window.zIndex}
      x={window.position.x}
      y={window.position.y}
      width={window.size.width}
      height={window.size.height}
      onMouseDown={handleMouseDown}
    >
      <TitleBar data-title-bar>
        <Title>{window.title}</Title>
        <WindowControls>
          <ControlButton 
            type="minimize" 
            onClick={() => onMinimize(window.id)}
            title="Minimize"
          >
            −
          </ControlButton>
          <ControlButton 
            type="maximize" 
            onClick={() => onMaximize(window.id)}
            title="Maximize"
          >
            □
          </ControlButton>
          <ControlButton 
            type="close" 
            onClick={() => onClose(window.id)}
            title="Close"
          >
            ×
          </ControlButton>
        </WindowControls>
      </TitleBar>

      <WindowContent>
        {children}
      </WindowContent>

      {!window.isMaximized && (
        <div
          style={{
            position: 'absolute',
            right: 0,
            bottom: 0,
            width: '20px',
            height: '20px',
            cursor: 'nw-resize',
            background: 'transparent'
          }}
          onMouseDown={handleResizeStart}
        />
      )}
    </WindowContainer>
  );
};

export default Window;
