import React from 'react';
import styled from 'styled-components';
import { Icon as IconType } from '../../types/desktop';

interface IconProps {
  icon: IconType;
  onClick: () => void;
  isSelected?: boolean;
}

const IconContainer = styled.div<{ isSelected: boolean; x: number; y: number }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  user-select: none;
  padding: 8px;
  border-radius: 8px;
  transition: all 0.2s ease;
  position: absolute;
  left: ${props => props.x}px;
  top: ${props => props.y}px;

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
    transform: scale(1.05);
  }

  ${props => props.isSelected && `
    background-color: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
  `}
`;

const IconCircle = styled.div<{ color: string; isImage: boolean }>`
  width: 64px;
  height: 64px;
  border-radius: ${props => props.isImage ? '12px' : '50%'};
  background-color: ${props => props.isImage ? 'transparent' : props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  font-size: ${props => props.isImage ? '0' : '32px'};
  color: white;
  font-weight: bold;
  overflow: hidden;
`;

const IconLabel = styled.div`
  color: white;
  text-align: center;
  font-size: 12px;
  font-weight: 500;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.8);
  max-width: 80px;
  word-wrap: break-word;
`;

const Icon: React.FC<IconProps> = ({ icon, onClick, isSelected = false }) => {
  return (
    <IconContainer 
      x={icon.position.x}
      y={icon.position.y}
      onClick={onClick}
      isSelected={isSelected}
    >
      <IconCircle color={icon.color}>
        {icon.icon}
      </IconCircle>
      <IconLabel>{icon.name}</IconLabel>
    </IconContainer>
  );
};

export default Icon;
